mapper.select:
		select * from producttype where visible=1
		 and name like #{name}
        order by typeid desc
		limit #{pageOffset},#{pageSize}

select t1.typeid, t1.name,t1.note,count(t2.typeid) as child from producttype as t1 left join producttype as t2 on t2.parentid=t1.typeid where t1.visible=1
and t1.parentid=#{parentid} and t1.parentid is null
GROUP BY t1.typeid order by t1.typeid limit #{pageOffset},#{pageSize}

select * from producttype where visible=1
 and parentid=#{parentid}

	order by ${order}

		${sort}


limit #{pageOffset},#{pageSize}

select p1.typeid as p1_typeid,p1.name as p1_name,p1.note as p1_note, p2.typeid as p2_typeid,p2.name as p2_name,p2.note as p2_note from producttype p1 left join producttype p2 on p1.parentid=p2.typeid where p1.visible=1
and p1.parentid=#{parentid} and p1.parentid is null
order by p1.typeid desc limit #{pageOffset},#{pageSize}

			select p2.typeid as typeid,p2.name as name,p2.note as note,p2.parentid as parentid 
               from producttype p1 left join producttype p2 on p1.parentid=p2.typeid
               where p1.typeid=#{childTypeId}
